<?phpclass Provider_ListController extends HM_Controller_Action{    use HM_Controller_Action_Trait_Grid;	    public function init() {        $this->_setForm(new HM_Form_Provider());        parent::init();    }        public function indexAction(){        $select = $this->getService('Provider')->getSelect();        $select->from(array('p' => 'providers'),            array(                'id' => 'p.id',                'title' => 'p.title'                )            )        ;                     $grid = $this->getGrid($select, array(            'id' => array('hidden' => true),            'title' => array(                'title' => _('Название')        	)        ),            array(                'title' => null            )        );        $grid->addAction(array(            'module' => 'provider',            'controller' => 'list',            'action' => 'edit'        ),            array('id'),            $this->view->svgIcon('edit', 'Редактировать')        );        $grid->addAction(array(            'module' => 'provider',            'controller' => 'list',            'action' => 'delete'        ),            array('id'),            $this->view->svgIcon('delete', 'Удалить')        );        $grid->addMassAction(array(            'module' => 'provider',            'controller' => 'list',            'action' => 'delete-by'        ),            _('Удалить'),            _('Данное действие может быть необратимым. Вы действительно хотите продолжить?')        );        $this->view->gridAjaxRequest = $this->isGridAjaxRequest();        $this->view->grid = $grid;    	    }    public function setDefaults(Zend_Form $form) {        $providerId = (int) $this->_request->getParam('id', 0);        $provider = $this->getService('Provider')->getOne($this->getService('Provider')->find($providerId));        if ($provider) {            $values = $provider->getValues();            $values['provider_id'] = $provid