<?phpclass Supplier_ListController extends HM_Controller_Action{    use HM_Controller_Action_Trait_Grid;	    public function init() {        $this->_setForm(new HM_Form_Supplier());        parent::init();    }        public function indexAction(){        $select = $this->getService('Supplier')->getSelect();        $select->from(array('s' => 'suppliers'),            array(                'supplier_id' => 's.supplier_id',                'title' => 's.title'                )            )        ;                     $grid = $this->getGrid($select, array(            'supplier_id' => array('hidden' => true),            'title' => array(                'title' => _('Название')        	)        ),            array(                'title' => null            )        );        $grid->addAction(array(            'module' => 'supplier',            'controller' => 'list',            'action' => 'edit'        ),            array('supplier_id'),            $this->view->svgIcon('edit', 'Редактировать')        );        $grid->addAction(array(            'module' => 'supplier',            'controller' => 'list',            'action' => 'delete'        ),            array('supplier_id'),            $this->view->svgIcon('delete', 'Удалить')        );        $grid->addMassAction(array(            'module' => 'supplier',            'controller' => 'list',            'action' => 'delete-by'        ),            _('Удалить'),            _('Данное действие может быть необратимым. Вы действительно хотите продолжить?')        );        $this->view->gridAjaxRequest = $this->isGridAjaxRequest();        $this->view->grid = $grid;    	    }    public function setDefaults(Zend_Form $form) {        $providerId = (int) $this->_request->getParam('supplier_id', 0);        $provider = $this->getService('Supplier')->getOne($this->getService('Supplier')->find($providerId));        if ($provider) {            $values = $provider->getValues();            $values['supplier_id'] = $providerId;            $form->populate($values);        }    }        public function create(Zend_Form $form) {    	        $provider = $this->getService('Supplier')->insert(            array(                'title' => $form->getValue('title'),                'address' => $form->getValue('address'),                'contacts' => $form->getValue('contacts'),                'description' => $form->getValue('description')            )        );            }        public function update(Zend_Form $form) {    	        $provider = $this->getService('Supplier')->update(            array(                'supplier_id' => $form->getValue('supplier_id'),                'title' => $form->getValue('title'),                'address' => $form->getValue('address'),                'contacts' => $form->getValue('contacts'),                'description' => $form->getValue('description')            )        );    }    public function delete($id) {        $this->getService('Supplier')->delete($id);    }        public function cardAction()    {        $supplierId = (int) $this->_getParam('supplier_id', 0);        $supplier = $this->getOne($this->getService('Supplier')->find($supplierId));        $this->view->supplier = $supplier;    }}