<script>
export default {
  props: ["type"],
  data() {
    return {
      iconName: null
    }
  },
  mounted() {
    this.getIcon()
  },
  methods: {
    getIcon() {
      if(this.type === 'Дата начала' || this.type === 'Дата окончания' || this.type === 'Ограничение времени обучения') this.iconName = 'calendar'
      else if(this.type === 'Место проведения') this.iconName = 'ring'
      else if(this.type === 'Тип') this.iconName = 'type'
      else if(this.type === 'Цена') this.iconName = 'price'
      else if(this.type === 'Цвет в календаре') this.iconName = 'color'
      else if(this.type === 'Тьютор') this.iconName = 'tutor'
      else if(this.type === 'Ссылка') this.iconName = 'link'
      console.log(this.type)
      console.log(this.iconName)
    }
  }
}
</script>
